import React from 'react';
import Modal from 'react-modal';

const EndScreenModal = ({ isOpen, totalPoints, closeModal }) => {
    return (
      <Modal
        isOpen={isOpen}
        onRequestClose={closeModal}
        contentLabel="End Screen"
        ariaHideApp={false}
        style={{
          overlay: {
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            backgroundColor: 'rgba(0, 0, 0, 0.7)',
          },
          content: {
            backgroundColor: '#8B4513', 
            color: '#FFF8DC', 
            padding: '20px',
            borderRadius: '10px',
          },
        }}
      >
        <div style={{ textAlign: 'center' }}>
          <h2 style={{ color: '#CD853F', fontSize: '24px' }}>Herzlichen Glückwunsch!</h2>
          <p style={{ fontSize: '18px' }}>Du hast alle Aufgaben richtig gelöst. Die Tür des letzten Raumes wurde geöffnet.</p>
          <p style={{ fontWeight: 'bold', fontSize: '20px' }}>Gesammelte Punkte: {totalPoints}</p>
          <button
            style={{
              backgroundColor: '#CD853F', 
              color: '#FFF8DC', 
              padding: '10px',
              borderRadius: '5px',
              cursor: 'pointer',
              fontSize: '16px',
            }}
            onClick={closeModal}
          >
            Schließen
          </button>
        </div>
      </Modal>
    );
  };

export default EndScreenModal;
