import React, { useState, useEffect } from 'react';
import './GameScreen.css';
import ASCIIExpl from './ASCIIExplain';
import UnicodeExpl from './UnicodeExplain';
import TaskScroll from './TaskScroll';

function GameScreen() {

  const levelBackgrounds = {
    1: require('./tempel.jpeg'),
    2: require('./tempel1.jpeg'),
    3: require('./tempel2.jpeg'),
    4: require('./tempel3.jpeg'),
    5: require('./tempel4.jpeg'),
    6: require('./tempel5.jpeg'),
    7: require('./tempel6.jpeg'),
    8: require('./tempel7.jpeg'),
    9: require('./tempel8.jpeg'),
    9: require('./tempel9.jpeg'),
    10: require('./tempel10.jpeg'),
  };

    const [currentLevel, setCurrentLevel] = useState("1");
    const [solution, setSolution] = useState("");
    const [showAsciiPopup, setShowAsciiPopup] = useState(false);
    const [showUnicodePopup, setShowUnicodePopup] = useState(false);
    const [score, setScore] = useState(0);
    const [message, setMessage] = useState("");
    const [scoreAnimation, setScoreAnimation] = useState(false);
    const [backgroundImage, setBackgroundImage] = useState("");

    const initializeBackgroundImage = () => {
      const initialBackgroundImage = levelBackgrounds[currentLevel];
      document.body.style.backgroundImage = `url(${initialBackgroundImage})`;
    };

    useEffect(() => {
      initializeBackgroundImage(); 
    }, []);


    const tasks = [
      {
        type: 'input',
        question: 'Deine erste Aufgabe auf dem Weg nach draußen, besteht darin, die Binärzahl 100000 in eine Dezimalzahl umzuformen.',
        correctAnswer:"a",
      },
      {
        type: 'input',
        question: 'Nachdem du im ersten Raum deine Kenntnisse über binäre Codierungen unter Beweis stellen konntest, ist es an der Zeit deine Kenntnisse zur ASCII Codierung zu überprüfen. Wie viele verschiedene Zeichen können mit 7-bit ASCII codiert werden?',
        correctAnswer:"a",
      },
      {
        type: 'multipleChoice',
        question: 'Du hast dich bis jetzt gut geschlagen und die ersten beiden Räume bereits hinter dir gelassen. Damit die nächste Tür sich öffnet, musst du aber auch deine Kenntnisse über Unicode beweisen. Wie viele Zeichen lassen sich aktuell mit Unicode codieren? Wähle die Antwort aus, die am nächsten dran ist.',
        options: {
          a: '128', b: '256', c: '15000', d: '150000',
        },
        correctAnswer:"a",
      },
      {
        type: 'multipleChoice',
        question:'Wie es aussieht, hast du die Grundlagen drauf. Dann wirst du sicherlich auch wissen, mit welcher Codierung man Smileys codieren kann',
        options: {
          a: 'ASCII', b: 'Unicode', c: 'beiden', d: 'keiner',
        },
        correctAnswer:"a",
      },
      {
        type: 'multipleChoice',
        question: 'Unicode ist ein wenig komplizierter als ASCII. In der Unicode Erklärung hast du wahrscheinlich schon gelesen, dass es UTF-8, UTF-16 und UTF-32 gibt. Aber hast du das ganze auch wirklich verstanden? Mit welchem Format lassen sich mehr einzelne Zeichen codieren? a) UTF-8, b) UTF-16, c) UTF-32, d) mit allen gleich viele',
        options: {
          a: 'ASCII', b: 'Unicode', c: 'beiden', d: 'keiner',
        },
        correctAnswer:"a",
      },
      {
        type: 'input',
        question: 'Kannst du auch die Zeichen entziffern, die hier im Raum an der Wand stehen? Mal sehen, ob du die Folgen aus 0en und 1en entziffern kannst. Für welches Zeichen steht der ASCII Code 1000110?',
        correctAnswer: "a",
      },
      {
        type: 'input',
        question: 'Jetzt will ich sehen, ob du auch selbst etwas an die Wand mit der Hilfe von 0en und 1en schreiben kannst. Wie lautet der 8-Bit ASCII-Code für das Wort Tempel?',
        correctAnswer:"a",
      },
      {
        type: 'input',
        question: 'Deine Rechenkünste sind nun gefragt. Wie viele einzelne Zeichen kann eine Zeichencodierung mit 16-bit codieren?',
        correctAnswer:"a",
      },
      {
        type: 'input',
        question: 'Jetzt hast du es fast geschafft. Jetzt musst du aber zeigen, dass du bis hierhin gut aufgepasst hast und dein neu erlangtes Wissen auch anwenden kannst. Welcher ASCII-Code repräsentiert das Sonderzeichen & ?',
        correctAnswer:"a",
      },
      {
        type: 'input',
        question: 'Dieser Text wurde im ASCII-Code dargestellt: 48 65 6c 6c 6f 21. Um die letzte Tür zu öffnen musst du dieses Wort dekodieren',
        correctAnswer:"a"
      }
      ];

      useEffect(() => {
        if (score !== 0) {
          setScoreAnimation(true);
          setTimeout(() => {
            setScoreAnimation(false);
          }, 1000); 
        }
      }, [score]);

    const handleLevelClick = (level) => {
    console.log("Handle Level Click:", level);
    setCurrentLevel(level);
    const body = document.querySelector('body');
    body.style.backgroundImage = `url(${levelBackgrounds[level]})`;
    setBackgroundImage(`url(${levelBackgrounds[level]})`);
    };


    const handleSubmit = () => {
        if (solution.trim() === tasks[currentLevel].correctAnswer) {
          setScore((prevScore) => prevScore + 10);
          setMessage("Richtig! Du hast 10 Punkte für diese Antwort bekommen.");
          setCurrentLevel((prevLevel) => prevLevel + 1); 
        } else {
          setMessage("Leider falsch! Schaue dir noch einmal die Erklärungen an.");
          setScore((prevScore) => (prevScore >= 5 ? prevScore - 5 : 0));
        }
        setSolution("");
      };

      const handleOptionClick = (selectedOption) => {
        const optionElement = document.getElementById(selectedOption);;

  if (optionElement) {
    // Hintergrundfarbe ändern, um die Auswahl anzuzeigen
    optionElement.classList.add("glowing");
        setTimeout(() => {
          optionElement.classList.remove("glowing");
        if (selectedOption === tasks[currentLevel].correctAnswer) {
          // Die richtige Antwort wurde ausgewählt
          setScore((prevScore) => prevScore + 10);
          setMessage("Richtig! Du hast 10 Punkte für diese Antwort bekommen.");
          setCurrentLevel((prevLevel) => prevLevel + 1);
        } else {
          // Falsche Antwort
          setMessage("Leider falsch! Schaue dir noch einmal die Erklärungen an.");
          setScore((prevScore) => (prevScore >= 5 ? prevScore - 5 : 0));
        }
        setSolution("");
      }, 2500);
    }
  }
      return (
        <div className="game-container">
          <div className="level-bar">
            {Array.from({ length: 10 }, (_, index) => (
              <div
                key={index}
                className={`level-indicator ${index === currentLevel ? 'current-level' : ''}`}
                onClick={() => handleLevelClick(index)}
              >
                {index + 1}
              </div>
            ))}
          </div>
          <div className="task-container">
            <div className="task-text">
              <TaskScroll taskText={tasks[currentLevel].question} />
              {tasks[currentLevel].type === 'multipleChoice' && (
                <div className="options-container">
                  {Object.entries(tasks[currentLevel].options).map(([key, value]) => (
                    <div key={key} id={key} className="option" onClick={() => handleOptionClick(key)}>
                      {`${key}) ${value}`}
                    </div>
                  ))}
                </div>
              )}
            </div>
            {tasks[currentLevel].type === 'input' && (
              <div>
                <input
                  type="text"
                  className="input-field"
                  value={solution}
                  onChange={(e) => setSolution(e.target.value)}
                />
                <button className="buttonSubmit" onClick={handleSubmit}>
                Antwort bestätigen
                </button>
              </div>
            )}
            {message && (
              <div className="popup">
                <div className="popup-content">
                  <h2>{message}</h2>
                  <button onClick={() => setMessage("")}>Schließen</button>
                </div>
              </div>
            )}
          </div>
          <div className="button-container">
            <button className="button" onClick={() => setShowAsciiPopup(true)}>
              ASCII Erklärung
            </button>
            <button className="button" onClick={() => setShowUnicodePopup(true)}>
              Unicode Erklärung
            </button>
          </div>
          {showAsciiPopup && (
            <div className="popup">
              <div className="popup-content">
                <div className="popup-header">
                  <h2>ASCII Erklärung</h2>
                  <button className="close" onClick={() => setShowAsciiPopup(false)}>
                    X
                  </button>
                </div>
                <ASCIIExpl />
                <button className="button" onClick={() => setShowAsciiPopup(false)}>
                  Schließen
                </button>
              </div>
            </div>
          )}
          {showUnicodePopup && (
            <div className="popup">
              <div className="popup-content">
                <h2>Unicode Erklärung</h2>
                <UnicodeExpl />
                <button className="button" onClick={() => setShowUnicodePopup(false)}>
                  Schließen
                </button>
              </div>
            </div>
          )}
        </div>
      );
          }      
export default GameScreen;
