import React, { useState, useEffect } from 'react';
import GameScreen from './GameScreen';
import './GameScreen.css';
import './App.css';

function App() {

 const initialBackgroundImage = require('./pictures/eingang.jpeg');
const [showPopup, setShowPopup] = useState(true);


    const closePopup = () => {
    setShowPopup(false);
  };




  return (
    <div className="App">
      {showPopup ? (
        <div className="Background" style={{ backgroundImage: `url(${initialBackgroundImage})`, backgroundSize: '100% 100%', backgroundRepeat: 'no-repeat', backgroundPosition: 'center center' }}>
        <div className="popup-container">
          <div className="popup">
            <div className="popup-content">
              <button className='button' onClick={closePopup}>Spiel Starten</button>
            </div>
          </div>
        </div>
        </div>
      ) : (
        <header className="App-header">
          <div>
          </div>
          <GameScreen/>
        </header>
      )}
    </div>
  );
 }


export default App;
