import React, {useState} from 'react';
import GameScreen from './GameScreen';
import './GameScreen.css';
import './Eingang.jpg';
import './App.css';
import EingangImage from './Eingang.jpg';

function App() {

const [showPopup, setShowPopup] = useState(true);

  const closePopup = () => {
    setShowPopup(false);
  };

  useEffect(() => {
    // Ändere das Hintergrundbild, wenn das Popup geöffnet ist
    if (showPopup) {
      document.body.style.backgroundImage = 'url("Eingang.jpg")';
      document.body.style.backgroundSize = 'cover'; // Anpassen, wie das Bild skaliert wird
    } else {
      document.body.style.backgroundImage = 'none';
    }
    return () => {
      document.body.style.backgroundImage = 'none';
    };
  }, [showPopup]);


  return (
    <div className="App" >
      {showPopup ? (
        <div className="popup-container">
          <div className="popup">
            <div className="popup-content">
              <h2 id='heading'>Willkommen zum ASCII und Unicode Lernspiel</h2>
              <p id='text'>Schaffst du es alle Rätsel zu lösen um aus dem Tempel zu entkommen?
              Auf deinem Weg nach draußen musst du 10 verschiedene Räume durchqueren.
              In jedem Raum warten unterschiedliche Aufgaben auf dich, die du lösen musst, 
              um deinen Weg raus zu finden.
              Jeder einzelne Raum wird durch ein Level in diesem Spiel repräsentiert.
              Mit jeder korrekten Antwort sammelst du Punkte für dein Punktekonto. Aber Vorsicht!
              Sei nicht zu schnell. Für jede falsche Antwort bekommst du nämlich Punkte wieder abgezogen.
              Für deinen Weg nach draußen gebe ich dir zwei Schriftrollen mit, die dir behilflich sein könnten. 
              Bist du dir bei einem Rätsel unsicher, empfehle ich dir dort reinzuschauen. 
              An der Leiste, die du oben sehen kannst, kannst du erkennen auf welchem Level du dich befindest und wie viele
              du noch vor dir hast. 

              Viel Erfolg!
              </p>
              <button className='button' onClick={closePopup}>Spiel Starten</button>
            </div>
          </div>
        </div>
      ) : (
        <header className="App-header" style={{ backgroundColor: 'transparent' }}>
          <div>
          </div>
          <GameScreen/>
        </header>
      )}
    </div>
  );
 }


export default App;
