import React, { useState, useEffect } from 'react';
import './GameScreen.css';
import ASCIIExpl from './ASCIIExplain';
import UnicodeExpl from './UnicodeExplain';

function GameScreen() {

  const levelBackgrounds = {
    1: require('./tempel.jpeg'),
    2: require('./tempel1.jpeg'),
    3: require('./tempel2.jpeg'),
    4: require('./tempel3.jpeg'),
    5: require('./tempel4.jpeg'),
    6: require('./tempel5.jpeg'),
    7: require('./tempel6.jpeg'),
    8: require('./tempel7.jpeg'),
    9: require('./tempel8.jpeg'),
    9: require('./tempel9.jpeg'),
  };

    const [currentLevel, setCurrentLevel] = useState("");
    const [solution, setSolution] = useState("");
    const [showAsciiPopup, setShowAsciiPopup] = useState(false);
    const [showUnicodePopup, setShowUnicodePopup] = useState(false);
    const [score, setScore] = useState(0);
    const [message, setMessage] = useState("");
    const [scoreAnimation, setScoreAnimation] = useState(false);
    const [backgroundImage, setBackgroundImage] = useState(`url(${levelBackgrounds[1]})`);

//was ist 100000 als Dezimalzahl
// wie viele zeichen sind auf der Tastatur 
// flexiblere Regeln für die Aufgaben um die Lösungen zu +überprüfen:
// Gesamtbild durch Lösungen am Ende
// Die antwortergebnisse sollten nicht direkt unterschieden werden 
/// Binärzahlen mit 
// hexadezimal nicht mehr zu kompliziert 
// UTF 8 und 16 und UTF 32 
// Am ende aus den Ergebnissen eines Leveles 
// randomierst 
//reihenfolge ändern um cheating entgegenzuwirken


    const tasks = {
        1: "Konvertiere die Dezimalzahl 10 in eine Binärzahl",
        2: "Berechne 2 hoch 5",
        3: "Wie viele verschiedene Zeichen können mit 7-bit ASCII codiert werden?",
        4: "Welche Dezimalzahl repräsentiert das Hexadezimalzeichen 'A'?",
        5: "Welcher Unicode-Code repräsentiert das Zeichen 'ß'",
        6: "Wie lautet der ASCII-Code (7-bit binärzahl) für den Buchstaben F?",
        7: "Das Wort Tempel soll in ASCII codiert werden. Wie viele Bits werden dafür benötigt?",
        8: "Wie viele einzelne Zeichen kann eine Zeichencodierung mit 16-bit codieren?",
        9: "Welcher ASCII-Code repräsentiert das Sonderzeichen '&'? Und welcher Unicode-Code repräsentiert dasselbe Zeichen?",
        10: "Dieser Text wurde im ASCII-Code dargestellt: 48 65 6c 6c 6f 21. Dekodiere dieses Wort"
      };

      const correctAnswers = {
        1: "1010",
        2: "32", 
        3: "128",
        4: "10",
        5: "U+00DF",
        6: "1000110",
        7: "40",
        8: "65 536",
        9: "",
        10: "Hello",
      };


      useEffect(() => {
        if (score !== 0) {
          setScoreAnimation(true);
          setTimeout(() => {
            setScoreAnimation(false);
          }, 1000); 
        }
      }, [score]);

    const handleLevelClick = (level) => {
      console.log("Handle Level Click:", level);
    setCurrentLevel(level);
    const body = document.querySelector('body');
    body.style.backgroundImage = `url(${levelBackgrounds[level]})`;
    setBackgroundImage(`url(${levelBackgrounds[level]})`);
    };

    handleLevelClick();

    const handleSubmit = () => {
        if (solution.trim() === correctAnswers[currentLevel]) {
          setScore((prevScore) => prevScore + 10);
          setMessage("Richtig! Du hast 10 Punkte für diese Antwort bekommen.");
          setCurrentLevel((prevLevel) => prevLevel + 1); 
        } else {
          setMessage("Leider falsch! Schaue dir noch einmal die Erklärungen an.");
          setScore((prevScore) => (prevScore >= 5 ? prevScore - 5 : 0));
        }
        setSolution("");
      };

    return (
        <div className="game-container">
          <div className="level-bar">
            {Array.from({ length: 10 }, (_, index) => (
            <div
            key={index + 1}
            className={`level-indicator ${index + 1 === currentLevel ? 'current-level' : ''}`}
            onClick={() => handleLevelClick(index + 1)}
             >
            {index + 1}
          </div>
        ))}
        <div className={`score-container ${scoreAnimation ? 'score-animation' : ''}`}>
            <span className="score-text">Punktestand:</span>
            <span className="score-value">{score}</span>
            </div>
      </div>
          <div className="task-container">
            <div className="task-text">
             {tasks[currentLevel]}
            </div>
            <input
          type="text"
          className="input-field"
          value={solution}
          onChange={(e) => setSolution(e.target.value)}
        />
        {message && (
        <div className="popup">
          <div className="popup-content">
            <h2>{message}</h2>
            <button onClick={() => setMessage("")}>Schließen</button>
          </div>
        </div>
      )}
        <button className="button" onClick={handleSubmit}>Abschicken</button>
          </div>
          <div className="button-container">
          <button className="button"  onClick={() => setShowAsciiPopup(true)}>ASCII Erklärung</button>
          <button className="button"  onClick={() => setShowUnicodePopup(true)}>Unicode Erklärung</button>
          </div>
          {showAsciiPopup && (
        <div className="popup">
          <div className="popup-content">
            <div className="popup-header">
                <h2>ASCII Erklärung</h2>
                <button className="close" onClick={() => setShowAsciiPopup(false)}>X</button>
            </div>
            <ASCIIExpl />
            <button className="button" onClick={() => setShowAsciiPopup(false)}>Schließen</button>
          </div>
        </div>
      )}
      {showUnicodePopup && (
        <div className="popup">
          <div className="popup-content">
            <h2>Unicode Erklärung</h2>
            <UnicodeExpl/>
            <button onClick={() => setShowUnicodePopup(false)}>Schließen</button>
          </div>
        </div>
      )}
        </div>
      );
  }

export default GameScreen;
