import React from 'react';
import Modal from 'react-modal';

const EndScreenModal = ({ isOpen, totalPoints, closeModal }) => {
    return (
      <Modal
        isOpen={isOpen}
        onRequestClose={closeModal}
        contentLabel="End Screen"
        ariaHideApp={false}
        style={{
          content: {
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            backgroundColor: '#8B4513', 
            color: '#FFF8DC', 
            padding: '20px',
          },
        }}
      >
        <h2 style={{ color: '#CD853F', fontSize: '24px' }}>Herzlichen Glückwunsch!</h2>
        <p style={{ fontSize: '18px' }}>Du hast alle Aufgaben richtig gelöst!</p>
        <p style={{ fontWeight: 'bold', fontSize: '20px' }}>Gesammelte Punkte: {totalPoints}</p>
        <button
          style={{
            backgroundColor: '#CD853F', 
            color: '#FFF8DC', 
            padding: '10px',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '16px', 
          }}
          onClick={closeModal}
        >
          Schließen
        </button>
      </Modal>
    );
  };

export default EndScreenModal;
